      },
      {
        role: "user",
        content: $node["prompt"].json.prompt // Use correct previous node name
      }
    ],
    temperature: 0.8
  }
};

const inputAll = $input.all();

// Find the item from 'Prepare for LLM'
const prepareNode = inputAll.find(i => i.source?.node === 'Prepare for LLM');
const simulateNode = inputAll.find(i => i.source?.node === 'Simulate HubSpot API');

// Get user message safely
const userMessage = prepareNode?.json?.body?.Body || "Hi there!";

// Get product/promo safely
const product = simulateNode?.json?.productRecommendation || "Let me know what kind of shoes you're looking for!";
const promo = simulateNode?.json?.promotionInfo || "I'll check if there's any special deal for you ðŸ˜‰";

// Return the combined prompt
return [
  {
    json: {
      prompt: `User message: ${userMessage}\nSuggested Product: ${product}\nPromotion: ${promo}`
    }
  }
];

// Filter out auto-follow-ups (e.g., where user message is "No response")
const filtered = $input.all().filter(i => i.json["User Message"]?.trim().toLowerCase() !== "no response");

// Sort by timestamp
const sortedItems = filtered.sort((a, b) => {
  return new Date(a.json.Timestamp).getTime() - new Date(b.json.Timestamp).getTime();
});

const latestItem = sortedItems[sortedItems.length - 1];
const previousItems = sortedItems.slice(0, -1);

const history = previousItems.map(item => {
  return `User: ${item.json["User Message"]}\nBob: ${item.json["Bot Reply"]}`;
}).join("\n");

const latest = latestItem?.json["User Message"] || "";

return [
  {
    json: {
      ChatHistory: history,
      LatestUserMessage: latest
    }
  }
];

